  <footer className="mediacard-footer">
    <small className="mediacard-footer-text">Leer más</small>
    <div>
      <small>37️</small>
      <img src={heart} className="heart" alt="heart" />
    </div>
  </footer>
);

class MediaCards extends React.Component {
  render() {
    return (
      <div className="mediacard">
        {headerMediaCards}
        {mainMediaCards}
        {footerMediaCards}
      </div>
    );
  }
}

class UserName extends React.Component {
  render() {
    console.log(nameToRender);
    return <h1 className="mediacard-title">{this.props.name}</h1>;
  }
}
const nameToRender = <UserName name="Ana Montiaga" />;

